function recentLFM(user,apikey,limit,container){var html="",counter=0,list='<ul class="lfm-recent"></ul>',divider=" &mdash; ",nullalbum="/wp-content/uploads/2016/03/smokehaus-album-cover.png";$.getJSON("http://ws.audioscrobbler.com/2.0/?method=user.getRecentTracks&user="+user+"&api_key="+apikey+"&limit="+limit+"&format=json&callback=?",function(data){$.each(data.recenttracks.track,function(i,item){var artistlink=item.url.split("_/");limit>counter&&(html+=""==item.image[1]["#text"]?'<li class="lfm-track"><a href="'+item.url+'" class="lfm-artwork" target="_blank"><img src="'+nullalbum+'" alt="'+item.album["#text"]+'" /></a><span class="lfm-trackname"><a href="'+item.url+'">'+item.name+"</a></span>"+divider+'<span class="lfm-artist"><a href="'+artistlink[0]+'" target="_blank">'+item.artist["#text"]+"</a></span></li>":'<li class="lfm-track"><a href="'+item.url+'" class="lfm-artwork" target="_blank"><img src="'+item.image[2]["#text"]+'" alt="'+item.album["#text"]+'" /></a><span class="lfm-trackname"><a href="'+item.url+'">'+item.name+"</a></span>"+divider+'<span class="lfm-artist"><a href="'+artistlink[0]+'" target="_blank">'+item.artist["#text"]+"</a></span></li>"),counter++}),$(container).append(html).wrapInner(list)})}$(document).ready(function(){$(".listening-to").length&&$.each($(".listening-to"),function(index,value){var user=$(this).data("lfm-user"),apikey=$(this).data("lfm-api"),limit=$(this).data("lfm-limit");recentLFM(user,apikey,limit,this)})});